INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78491, 0, 0, "%s commence à canaliser |cFFFF0404|Hspell:159996|h[Spores infestantes]|h|r !", "%s beginnt, |cFFFF0404|Hspell:159996|h[Sporenbefall]|h|r zu kanalisieren!", "¡%s comienza a canalizar |cFFFF0404|Hspell:159996|h[Esporas infectantes]|h|r!", "%s начинает поддерживать заклинание |cFFFF0404|Hspell:159996|h["Заражение спорами"]|h|r!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83378, 0, 0, "Après ce combat, ce sera plus que de la purée.", "Die werden ausseh'n wie Blutwurst nach dem Kampf.", "Ellos verse como charcos después de esta pelea.", "От них останется только мокрое место.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83378, 1, 0, "J'espère que ces avortons vont mourir mieux qu'ils sentent.", "Hoffentlich diese Schwächlinge besser sterben als riechen.", "Esperar que estos debiluchos morir mejor de lo que oler.", "Надеюсь, умирают они лучше, чем выглядят.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83378, 2, 0, "HAHAHAHAHA !", "HAHAHAHAHA!", "¡JA, JA, JA, JA, JA, JA!", "ХА-ХА-ХА-ХА-ХА!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83378, 3, 0, "Mais on avait pas gagné la bataille des Flèches-Rouges ?", "Wir Schlacht nich' gewonnen?", "¿No haber ganado Batalla de los pináculos rojos?", "Разве на Красных Пиках выиграли не мы?");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83378, 4, 0, "S'ils respectent pas notre histoire, on les invitera plus à nos jeux.", "Nich' unsere Tradition missachten, oder nächstes Jahr ihr nich' dabei sein.", "Faltar respeto a nuestra herencia no lograr que invitarlos a juegos del próximo año.", "Уважайте нашу историю, или не позовем вас на следующие игры!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83378, 5, 0, "Comment les minus ont battu puissant Vul'gor ?!", "Wie die kleinen Burschen mächtigen Vul'gor besiegen konnten?", "¡¿Cómo ser que hombres pequeños derrotar a poderoso Vul'gor?!", "Как эти щенки побили Вул'гора?!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83377, 0, 0, "Ça doit être une erreur ! Ces ptits trucs tout mous, c'est le fléau de Nagrand ? On dirait du popo d'elekk.", "Das kann doch nich' stimmen! Diese Mickerchen soll'n die Geißel von Nagrand sein? Sehen wie Elekkköttel aus!", "¡Tener que haber un error! ¿Estos blandengues ser el azote de Nagrand? Parecer desechos de elekk.", "Это, должно быть, ошибка! Эти жалкие черви  гроза агранда? а они в плевке элекка утонут.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83377, 1, 0, "Ah ! Les jeux d'aujourd'hui honorent le grand roi-sorcier ! On va voir sa victoire sanglante sur les orcs à la bataille des Flèches-Rouges !", "Ha! Die heutigen Spiele ehren großen Zaubererkönig! Wir werden sehen seinen blutigen Sieg in der Schlacht der Roten Spitzen!", "¡Ja! ¡Los juegos de hoy honrar al gran Rey hechicero! ¡Veremos su victoria sangrienta sobre los orcos en la Batalla de los pináculos rojos!", "Сегодня игры проводятся в честь короля-чародея! ы увидим его победу над орками при расных иках!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83377, 2, 0, "On va voir de quoi les adversaires sont faits, Thoktar. On le saura dès qu'ils auront fait « splash » dans toute l'arène ! HAHAHAHA !", "Wir werden sehen, woraus diese Herausforderer gemacht sind, Thoktar. ...sobald das, woraus sie gemacht sind, den Arenaboden verziert! HAHAHAHA!", "Nosotros ver de qué estar hechos estos competidores, Thoktar, ¡en cuanto su relleno quedar esparcido por toda arena! ¡JA, JA, JA, JA!", "Посмотрим, Токтар, как они будут извиваться и молить о пощаде, когда их станут расплющивать по всей арене! Ха-ха-ха!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83377, 3, 0, "Dans le rôle de notre puissant roi, scandez son nom et écrasez un crâne pour l'Ombre de Cognefort, le champion des jeux Vul'gor !", "Jetzt, in der Rolle unseres mächtigen Kaisers, brüllt seinen Namen und schlagt einen Schädel ein für den Schatten von Hochfels, Vul'gor!", "Ahora tú actuar como nuestro poderoso rey, ¡gritar su nombre y aplastar un cráneo por la Sombra de Gran Magullador, campeón de los juegos, Vul'gor!", "А теперь – трепещите! В роли нашего могучего короля на арену выходит чемпион игр – Вул'гор!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83377, 4, 0, "C'est l'heure de verser le sang et de mourir pour Cognefort ! La bataille des Flèches-Rouges commence !", "Vergießt Blut und sterbt glorreich zu Ehren von Hochfels! Die Schlacht der Roten Spitzen beginnt!", "¡Ser hora de derramar sangre y morir por Gran Magullador! ¡La Batalla de los pináculos rojos comenzar!", "Во имя Верховного Молота! Пусть начнется битва при Красных Пиках!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83377, 5, 0, "Ces mauviettes arrachent plus de membres qu'ils en perdent !", "Diese Winzlinge teilen ja mehr aus, als sie einstecken!", "¡Estos debiluchos estar llevándose más extremidades de las que perder!", "Они отсекли больше конечностей, чем потеряли!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83377, 6, 0, "On dirait qu'ils ont pas compris qu'ils devaient mourir.", "Scheinbar ist denen nicht klar, dass sie sterben sollen.", "Parecer no importarles que ellos deber morir.", "Кажется, они не знают, что им положено умереть.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83377, 7, 0, "Les gringalets... ont battu Vul'gor ?!", "Die Winzlinge... Vul'gor besiegt?", "Los debiluchos... ¡¿haber derrotado Vul'gor?!", "Слабаки... победили ул'гора?!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83377, 8, 0, "Le roi-sorcier ?! Ici ?! Le roi quitte le palais pour voir que les combats légendaires !", "Der Zaubererkönig!? Hier!? König verlassen Palast nur, um legendäre Kämpfe zu sehen!", "¡¿El Rey hechicero?! ¡¿Aquí!?! ¡El Rey solo salir del palacio para presenciar batallas legendarias!", "Король-чародей?! Здесь?! Он же покидает дворец только ради великих битв!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83268, 0, 0, "Votre présence nous honore, Kargath. Je vais prendre plaisir à vous regarder combattre à nouveau, malgré votre... handicap.", "Ihr ehrt uns, Kargath. Ich werde Euch mit Freuden wieder in der Arena kämpfen sehen, selbst mit Eurer... Behinderung.", "Nos honras, Kargath. Disfrutaré volver a verte pelear en la arena, aun con tu... limitación.", "Какая честь, Каргат. Я с удовольствием посмотрю, как ты снова будешь биться, несмотря на… увечье.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83268, 1, 0, "Gladiateurs ! Mourez avec panache et vos noms resteront gravés dans l'histoire. Que le combat commence !", "Gladiatoren! Ergreift diese Gelegenheit, glorreich zu sterben, denn eure Namen werden in die Geschichte eingehen. Lasst die Spiele beginnen!", "¡Gladiadores! Aprovechen la oportunidad de morir con gloria, pues sus nombres se grabarán en la historia. ¡Que comience la batalla!", "Гладиаторы! Я дарую вам шанс заслужить славную смерть и навеки вписать свое имя в историю! Да начнется битва!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (83268, 2, 0, "Achevez-le.", "Macht ihn fertig.", "Acaba con él.", "Прикончите его.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (86609, 0, 0, "Rhah ! Ces lance-flammes ne servent à rien, on se fait massacrer !", "Argh! Diese Flammenwerfer sind nutzlos. Wir werden verschlungen!", "¡Argh! ¡Estos lanzallamas son inútiles! ¡Nos está comiendo vivos!", "Р-р-р! Эти огнеметы бесполезны. Нас сейчас съедят!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (86609, 1, 0, "Repliez-vous ! Jetez vos armes et remontez à bord !", "Rückzug! Lasst die Waffen fallen und flüchtet auf das Schiff!", "¡Atrás! ¡Suelten sus armas y retírense a la nave!", "Отступаем! Бросайте оружие и на корабль!.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (80048, 0, 0, "Gloire et mort !", "Ruhm und Tod!", "¡Gloria y muerte!", "Смерть и слава!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (80048, 1, 0, "Boucherie !", "Geschlachtet!", "¡Destrozado!", "На куски!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (80048, 1, 1, "Moi déchirer, toi mourir !", "Ich reiße, Ihr sterbt!", "¡Yo destripar, tú morir!", "Сдохни!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (80048, 2, 0, "Casser vous !", "Zerfetzen!", "¡Destruirte!", "Раздавлю!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 0, 0, "Assez ! Je vais montrer à cette vermine ce dont est capable un vrai champion.", "Genug! Ich zeige diesen Schwächlingen, wozu ein wahrer Champion in der Lage ist!", "¡Basta! Le mostraré a estos debiluchos lo que un verdadero campeón puede hacer.", "Довольно! Я покажу этим щенкам, на что способен истинный чемпион.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 1, 0, "Le plaisir est pour moi. La foule mérite un meilleur gladiateur que le précédent, tombé si facilement.", "Die Freude ist ganz meinerseits. Die Menge verdient einen überlegenen Gladiator, wo doch der letzte so leicht zu besiegen war.", "El honor es mío. Las multitudes merecen un gladiador de altura después de ver la facilidad con que cayó el último.", "Я польщен. Зрители заслуживают настоящего гладиатора, последний боец был просто бездарен.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 2, 0, "Vous me devez un combat, bande de lâches !", "Ihr schuldet mir einen Kampf, Feiglinge.", "Me deben una pelea, cobardes.", "Начнем бой, трусы!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 3, 0, "Pas d'échappatoire", "Es gibt kein Entkommen.", "No hay escapatoria.", "Спасения нет!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 4, 0, "Donnons à la foule quelque chose à se mettre sous la dent.", "Die Menge will Blut sehen.", "¡Démosle a la multitud un trozo de la acción!", "Народ жаждет зрелищ, и он их получит!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 5, 0, "Il est temps de faire couler le sang !", "Jetzt seid Ihr dran!", "¡Hora de derramar tu sangre!", "Пора пролить вашу кровь!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 6, 0, "J'ai joué avec vous assez longtemps !", "Genug Spielereien.", "¡Ya me he divertido mucho tiempo!", "Мне надоело играть с вами!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 7, 0, "Vous croyiez que ça allait marcher ?! C'est vous qui allez brûler !", "Ihr haltet Euch wohl für schlau. Ihr brennt als Nächstes!", "¡¿Te crees muy listo?! ¡Serás el siguiente en arder!", "Эй, умники! Гореть вам в пекле!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 7, 1, "Imbéciles ! Il vous faudra plus que des flammes pour m'achever !", "Narren! Um mich zu besiegen, braucht Ihr mehr als nur Feuer!", "¡Necio! ¡Se necesitan más que llamas para derrotarme!", "Глупцы! Этим жалким огнем меня не напугать!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 7, 2, "Lâches ! Vous ne pourrez pas vous cacher derrière ces pièges indéfiniment !", "Feigling! Ihr könnt Euch nicht ewig hinter Fallen verstecken!", "¡Cobarde! ¡No te puedes esconder tras esas trampas para siempre!", "Трусы! Вы не сможете вечно укрываться за ними!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 8, 0, "Vous vous cassez trop vite !", "Ihr seid so zerbrechlich!", "¡Es muy fácil destrozarte!", "Как легко вас сломать!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 8, 1, "Vous resterez dans les mémoires, comme ces saletés restent accrochées à mes bottes.", "Ihr werdet in die Geschichte eingehen. Als Dreck an meinem Stiefel.", "Siempre serás recordado como una mancha en mi bota.", "Придется потом отчищать от тебя ботинки.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 8, 2, "Pas de pitié !", "Keine Gnade!", "¡Sin piedad!", "Пощады не будет!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (78714, 9, 0, "Là... ça fait cent.", "Und das... sind Einhundert.", "Y con este... son cien.", "А-а-а-а-а... Теперь сто...");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 0, 0, "Venez donc, bande de casse-pieds maigrichons !", "Kommt her, ihr mickrigen Raffkes.", "¡Vengan acá, esqueléticos!", "Сюда, паршивцы!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 1, 0, "Plein d’os et pas de viande.", "Nur Haut und Knochen.", "Todos crujientes y sin carne.", "Мяса нет, жилы одни.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 2, 0, "Vous ferez un ragoût bien fade.", "Das gibt leckeren Fahleneintopf.", "Haré un estofado insulso.", "Бледное выйдет жаркое.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 3, 0, "Venez par ici !", "Versucht es doch!", "¡Vengan!", "Подходи по одному!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 4, 0, "Je vais vous saigner !", "Lasst das Blut fließen!", "¡Que la sangre corra!", "Выпустить всю кровь!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 5, 0, "Il est temps de vous passer au hachoir !", "Zeit für den Fleischwolf!", "¡Hora de triturar la carne!", "Пускаю в дело мясорубку!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 6, 0, "Juste une petite tranche !", "Nur eine Scheibe oben abschneiden!", "¡Solo una rebanada de arriba!", "Я только макушку срежу!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 7, 0, "Je briserai tous vos os !", "ICH WERDE EUCH BRECHEN!", "¡LOS APLASTARÉ!", "Я ВАС ЗАЛОМАЮ!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 8, 0, "De la viande juteuse !", "So saftig!", "¡Qué jugoso!", "Сочно!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 8, 1, "Je les préfère crus et frétillants !", "Gib ihn uns roh und zappelnd!", "¡Dánoslo, crudo y retorciéndose!", "Дай его нам живого, трепыхающегося!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 9, 0, "Il y a à nouveau de la viande au menu !", "Ab sofort steht wieder Fleisch auf dem Speiseplan!", "¡Parece que de nuevo hay carne en el menú!", "Мясо снова в меню!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 10, 0, "Avortons rachitiques et puants !", "Schlaksige, zähe Kümmerlinge...", "Desagradables insignificantes...", "Тощая, вонючая мелюзга...");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 11, 0, "Dégagez ! Vous ne valez même pas d'être mangés !", "Runter mit euch! Ihr seid kaum eine Mahlzeit wert!", "¡Basta! ¡No vale la pena comerlos!", "Пшли вон! В вас есть нечего!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77404, 12, 0, "Je vous écraserai !", "Zerschmettern!", "¡Machaaaaque!", "Шмя-а-а-ак!");
